import React, { useState, useEffect, Fragment } from "react";
import Greeting from "../Greeting/Greeting";
import Search from "../Search/Search";

const Cards = ({ name }) => {
  const [cards, setCards] = useState([]);
  const [searchCard, setSearchCard] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (event) => {
    setSearchCard(event.target.value);
  };

  useEffect(() => {
    fetch(
      "https://api.magicthegathering.io/v1/cards?random=true&pageSize=100&language=English"
    )
      .then((res) => res.json())
      .then((data) => {
        setCards(data.cards);
        console.log(data.cards);
      });
  }, []);

  useEffect(() => {
    const results = cards.filter(
      (card) =>
        card.name.toLowerCase().includes(searchCard) ||
        card.text.toLowerCase().includes(searchCard)
    );
    setSearchResults(results);
  }, [searchCard, cards]);

  return (
    <Fragment>
      <Search handleSearch={handleSearch} />
      <Greeting name={name} />
      {searchResults.map((card, i) => (
        <div key={i}>
          <p>{card.name}</p>
          <p>{card.type}</p>
        </div>
      ))}
    </Fragment>
  );
};

export default Cards;
